import { useState } from "react";
import { useAuth } from "../context/AuthContext";

export default function LoginPage({ onSwitchToRegister }: { onSwitchToRegister: () => void }) {
  const { login } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = () => {
    if (login(username, password)) {
      // success → React sẽ tự chuyển sang homepage vì user != null
    } else {
      setError("Đăng nhập thất bại!");
    }
  };

  return (
    <div>
      <h2>Đăng nhập</h2>
      <input placeholder="Tên đăng nhập" value={username} onChange={e => setUsername(e.target.value)} />
      <input placeholder="Mật khẩu" type="password" value={password} onChange={e => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Đăng nhập</button>
      <p style={{ color: "red" }}>{error}</p>
      <button onClick={onSwitchToRegister}>Chưa có tài khoản? Đăng ký</button>
    </div>
  );
}
