import React from "react";

interface InfoPanelProps {
  roomId: string;
  turn: "white" | "black";
  playerColor: "white" | "black" | null;
  gameOver: "white" | "black" | null;
}

export const InfoPanel: React.FC<InfoPanelProps> = ({ roomId, turn, playerColor, gameOver }) => {
  const isWhiteTurn = turn === "white";

  return (
    <div
      style={{
        width: "300px",
        padding: "20px",
        border: "2px solid #666",
        borderRadius: "12px",
        backgroundColor: "#1e1e1e",
        color: "#f0f0f0",
        boxShadow: "0 0 15px rgba(255,255,255,0.1)",
        fontFamily: "Segoe UI, sans-serif",
      }}
    >
      <h3
        style={{
          textAlign: "center",
          marginBottom: "15px",
          color: "#00d8ff",
          textShadow: "0 0 6px rgba(0,216,255,0.7)",
        }}
      >
        Thông tin phòng
      </h3>

      <p><strong>Mã phòng:</strong> <span style={{ color: "#fff" }}>{roomId}</span></p>

      <div
        style={{
          marginTop: "10px",
          textAlign: "center",
          fontWeight: "bold",
          backgroundColor: isWhiteTurn ? "#3a3a3a" : "#292929",
          color: isWhiteTurn ? "#ffffff" : "#f2b84b",
          padding: "8px",
          borderRadius: "6px",
        }}
      >
        Lượt hiện tại: {isWhiteTurn ? "Trắng ♔" : "Đen ♚"}
      </div>

      <div style={{ marginTop: "20px" }}>
        <h4
          style={{
            borderBottom: "1px solid #555",
            paddingBottom: "6px",
            color: "#00d8ff",
          }}
        >
          Người chơi
        </h4>
        <p>♔ <strong>Trắng:</strong> {playerColor === "white" ? "Bạn" : "Đối thủ"}</p>
        <p>♚ <strong>Đen:</strong> {playerColor === "black" ? "Bạn" : "Đối thủ"}</p>
      </div>

      {gameOver && (
        <div
          style={{
            marginTop: "30px",
            textAlign: "center",
            fontWeight: "bold",
            fontSize: "18px",
            color:
              gameOver === "white"
                ? "#66ccff"
                : gameOver === "black"
                ? "#ff6b6b"
                : "#aaa",
            textShadow: "0 0 8px rgba(255,255,255,0.4)",
          }}
        >
          {gameOver === "white"
            ? "Trắng thắng! ♔"
            : gameOver === "black"
            ? "Đen thắng! ♚"
            : "Hòa!"}
        </div>
      )}
    </div>
  );
};
