// ...existing code...
import { useState } from "react";
import { useAuth } from "../context/AuthContext";

export default function HomePage({ onEnterRoom }: { onEnterRoom: (roomId: string) => void }) {
  const { user, logout } = useAuth();
  const [roomId, setRoomId] = useState("");

  const styles: { [k: string]: React.CSSProperties } = {
    page: {
      minHeight: "100vh",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      background: "#f3f4f6",
      padding: 20,
    },
    card: {
      width: 420,
      background: "#fff",
      padding: 24,
      borderRadius: 12,
      boxShadow: "0 6px 18px rgba(15,23,42,0.08)",
      textAlign: "center",
    },
    avatar: {
      width: 64,
      height: 64,
      borderRadius: "50%",
      background: "#6366f1",
      color: "#fff",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: 22,
      fontWeight: 600,
      margin: "0 auto 12px",
    },
    title: {
      margin: "6px 0 18px",
      color: "#0f172a",
      fontSize: 20,
    },
    input: {
      width: "100%",
      padding: "10px 12px",
      borderRadius: 8,
      border: "1px solid #e5e7eb",
      marginBottom: 12,
      outline: "none",
      boxSizing: "border-box",
    },
    actions: {
      display: "flex",
      gap: 10,
      marginBottom: 12,
    },
    btn: {
      flex: 1,
      padding: "10px 12px",
      borderRadius: 8,
      border: "none",
      cursor: "pointer",
      fontWeight: 600,
    },
    primary: {
      background: "#6366f1",
      color: "#fff",
    },
    secondary: {
      background: "#fff",
      color: "#111827",
      border: "1px solid #e5e7eb",
    },
    logout: {
      marginTop: 8,
      fontSize: 14,
      color: "#9ca3af",
      background: "transparent",
      border: "none",
      cursor: "pointer",
    },
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <div style={styles.avatar}>{user?.username?.charAt(0)?.toUpperCase() || "U"}</div>
        <h2 style={styles.title}>Xin chào {user?.username}!</h2>

        <input
          style={styles.input}
          placeholder="Nhập mã phòng"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
          aria-label="Mã phòng"
        />

        <div style={styles.actions}>
          <button
            style={{ ...styles.btn, ...styles.primary }}
            onClick={() => onEnterRoom(roomId)}
          >
            Vào phòng
          </button>
          <button
            style={{ ...styles.btn, ...styles.secondary }}
            onClick={() => onEnterRoom("auto")}
          >
            Ghép ngẫu nhiên
          </button>
        </div>

        <button style={styles.logout} onClick={logout}>
          Đăng xuất
        </button>
      </div>
    </div>
  );
}
