import Square from "./Square";

const pieceImages: { [key: string]: string } = {
  P: "/pieces/wP.png",
  p: "/pieces/bP.png",
  R: "/pieces/wR.png",
  r: "/pieces/bR.png",
  N: "/pieces/wN.png",
  n: "/pieces/bN.png",
  B: "/pieces/wB.png",
  b: "/pieces/bB.png",
  Q: "/pieces/wQ.png",
  q: "/pieces/bQ.png",
  K: "/pieces/wK.png",
  k: "/pieces/bK.png",
};

const initialBoard = [
  "rnbqkbnr",
  "pppppppp",
  "........",
  "........",
  "........",
  "........",
  "PPPPPPPP",
  "RNBQKBNR",
];

const Board = () => {
  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(8, 60px)",
        gridTemplateRows: "repeat(8, 60px)",
        border: "2px solid black",
      }}
    >
      {initialBoard.map((row, i) =>
        row.split("").map((piece, j) => {
          const color = (i + j) % 2 === 0 ? "#f0d9b5" : "#b58863";
          const piecePath = pieceImages[piece];
          return <Square key={`${i}-${j}`} piece={piecePath} color={color} />;
        })
      )}
    </div>
  );
};

export default Board;
