import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import "./RegisterPage.css"; // đảm bảo file này nằm cùng thư mục hoặc chỉnh lại ./ nếu cần

export default function RegisterPage({ onSwitchToLogin }: { onSwitchToLogin: () => void }) {
  const { register } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleRegister = async () => {
    if (!username || !password) {
      setError("Vui lòng nhập đầy đủ thông tin!");
      return;
    }

    setError("");
    setLoading(true);

    try {
      const timeoutPromise = new Promise<boolean>((_, reject) => {
        setTimeout(() => reject(new Error("Timeout")), 10000);
      });

      const success = await Promise.race([
        register(username, password),
        timeoutPromise,
      ]);

      if (success) onSwitchToLogin();
      else setError("Tên tài khoản đã tồn tại!");
    } catch (err) {
      if (err instanceof Error && err.message === "Timeout")
        setError("Không thể kết nối tới server. Vui lòng thử lại sau!");
      else setError("Đã có lỗi xảy ra, vui lòng thử lại!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      <div className="register-card">
        {/* Left Section */}
        <div className="register-left">
          <h1 className="register-title">Tạo tài khoản mới</h1>
          <p className="register-subtitle">
            Tham gia cộng đồng cờ vua trực tuyến và chinh phục mọi đối thủ!
          </p>

          <input
            type="text"
            placeholder="Tên đăng nhập"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            disabled={loading}
            className="register-input"
          />

          <input
            type="password"
            placeholder="Mật khẩu"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            disabled={loading}
            className="register-input"
          />

          {error && <div className="error-box">{error}</div>}

          <button
            onClick={handleRegister}
            disabled={loading}
            className={`btn-register ${loading ? "btn-disabled" : ""}`}
          >
            {loading ? "Đang xử lý..." : "Tạo tài khoản"}
          </button>

          <div className="register-switch">
            <p>Đã có tài khoản?</p>
            <button onClick={onSwitchToLogin} disabled={loading}>
              Đăng nhập
            </button>
          </div>
        </div>

        {/* Right Section */}
        <div className="register-right">
          <img
            src="https://kurtmeyer.com/wp-content/uploads/2020/11/Board-with-pieces-03-1.jpg"
            alt="Chess Register"
            className="register-image"
          />
        </div>
      </div>
    </div>
  );
}
