import { useState, useEffect } from "react";
import Square from "./Square";
import { isValidMove } from "./chessRules";
import { useChessSocket } from "./useChessSocket";
import { playSound } from "../utils/soundManager";

const pieceImages: { [key: string]: string } = {
  P: "/pieces/wP.png", p: "/pieces/bP.png",
  R: "/pieces/wR.png", r: "/pieces/bR.png",
  N: "/pieces/wN.png", n: "/pieces/bN.png",
  B: "/pieces/wB.png", b: "/pieces/bB.png",
  Q: "/pieces/wQ.png", q: "/pieces/bQ.png",
  K: "/pieces/wK.png", k: "/pieces/bK.png",
};

const initialBoard: string[] = [
  "rnbqkbnr",
  "pppppppp",
  "........",
  "........",
  "........",
  "........",
  "PPPPPPPP",
  "RNBQKBNR",
];

const Board: React.FC<{ roomId: string }> = ({ roomId }) => {
  const [board, setBoard] = useState<string[][]>(initialBoard.map(r => r.split("")));
  const [selected, setSelected] = useState<[number, number] | null>(null);
  const [validMoves, setValidMoves] = useState<[number, number][]>([]);
  const [turn, setTurn] = useState<"white" | "black">("white");
  const [gameOver, setGameOver] = useState<null | "white" | "black">(null);

  const { sendMove, playerColor } = useChessSocket(roomId, (fen, newTurn, newGameOver, event) => {
    console.log("üì® Received FEN:", fen);

    const rows = fen.split(" ")[0].split("/").map(r =>
      r.split("").flatMap(c => isNaN(+c) ? [c] : Array(+c).fill("."))
    );
    setBoard(rows as string[][]);
    setTurn(newTurn);
    setGameOver(newGameOver);
    if (event) playSound(event); // üß© ph√°t nh·∫°c theo s·ª± ki·ªán
  });

  const handleClick = (r: number, c: number) => {
    if (gameOver || !playerColor) return;

    // Ch·ªâ cho di chuy·ªÉn khi ƒë√∫ng l∆∞·ª£t
    if (turn !== playerColor) return;

    const piece = board[r][c];
    const pieceColor = piece.toUpperCase() === piece ? "white" : "black";

    if (!selected) {
      if (piece !== "." && pieceColor === playerColor) {
        setSelected([r, c]);
        const moves: [number, number][] = [];
        for (let i = 0; i < 8; i++)
          for (let j = 0; j < 8; j++)
            if (isValidMove(piece, r, c, i, j, board))
              moves.push([i, j]);
        setValidMoves(moves);
      }
      return;
    }

    const [r1, c1] = selected;
    const selectedPiece = board[r1][c1];
    if (!selectedPiece || selectedPiece === ".") {
      setSelected(null);
      setValidMoves([]);
      return;
    }

    if (validMoves.some(([x, y]) => x === r && y === c)) {
      let moveUCI = `${String.fromCharCode(97 + c1)}${8 - r1}${String.fromCharCode(97 + c)}${8 - r}`;

        // üß© TH√äM X·ª¨ L√ù PHONG H·∫¨U:
      const isWhitePawn = selectedPiece === "P";
      const isBlackPawn = selectedPiece === "p";
      const promotionRow = isWhitePawn ? 0 : isBlackPawn ? 7 : -1;

      // n·∫øu t·ªët ƒëi ƒë·∫øn h√†ng cu·ªëi c√πng ‚Üí phong h·∫≠u m·∫∑c ƒë·ªãnh
      if ((isWhitePawn && r === 0) || (isBlackPawn && r === 7)) {
        moveUCI += "q"; // phong th√†nh h·∫≠u (queen)
      }


      sendMove(moveUCI);
    }

    setSelected(null);
    setValidMoves([]);
  };

  return (
    <div>
      {gameOver && <div style={{ color: "red", textAlign: "center", fontSize: "20px", marginBottom: "10px" }}>
        {gameOver === "white" ? "Tr·∫Øng th·∫Øng!" : gameOver === "black" ? "ƒêen th·∫Øng!" : "H√≤a!"}
      </div>}

      <div style={{ display: "grid", gridTemplateColumns: "repeat(8, 60px)", gridTemplateRows: "repeat(8, 60px)", border: "2px solid black" }}>
        {board.map((row, i) =>
          row.map((piece, j) => {
            const color = (i + j) % 2 === 0 ? "#f0d9b5" : "#b58863";
            const piecePath = pieceImages[piece] || "";
            const isSelected = selected?.[0] === i && selected?.[1] === j;
            const isValid = validMoves.some(([x, y]) => x === i && y === j);
            const bgColor = isSelected ? "yellow" : isValid ? "#a0d8ff" : color;
            return <Square key={`${i}-${j}`} value={piecePath} isSelected={isSelected} onClick={() => handleClick(i, j)} color={bgColor} />;
          })
        )}
      </div>
    </div>
  );
};

export default Board;
