import React, { useEffect, useRef, useState } from "react";

const CELL_SIZE = 80;
const ROWS = 8, COLS = 8;
const WS_URL = "ws://192.168.1.9:8765";

// Emoji chess pieces
const PIECES: Record<string, string> = {
  P: "‚ôô", p: "‚ôü",
  R: "‚ôñ", r: "‚ôú",
  N: "‚ôò", n: "‚ôû",
  B: "‚ôó", b: "‚ôù",
  Q: "‚ôï", q: "‚ôõ",
  K: "‚ôî", k: "‚ôö",
};

// Convert row,col ‚Üí chess square
function coordToSquare(row: number, col: number) {
  const file = String.fromCharCode("a".charCodeAt(0) + col);
  const rank = 8 - row;
  return `${file}${rank}`;
}

export default function ChessClient() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [ws, setWs] = useState<WebSocket | null>(null);
  const [connected, setConnected] = useState(false);

  // B√†n c·ªù: 8x8 string (P/p/N/n/...)
  const [board, setBoard] = useState(
    Array.from({ length: ROWS }, (_, r) =>
      Array.from({ length: COLS }, (_, c) => "")
    )
  );
  const [selected, setSelected] = useState<[number, number] | null>(null);

  // WebSocket
  useEffect(() => {
    const socket = new WebSocket(WS_URL);

    socket.onopen = () => {
      console.log("Connected to server");
      setConnected(true);
      socket.send(JSON.stringify({ type: "join", room: "room1" }));
    };

    socket.onmessage = (msg) => {
      const data = JSON.parse(msg.data);
      console.log("Received:", data);

      if (data.type === "board") {
        // Server g·ª≠i nguy√™n b√†n c·ªù (matrix)
        setBoard(data.board);
      }

      if (data.type === "move") {
        const { from, to, piece } = data;
        setBoard(prev => {
          const newBoard = prev.map(row => [...row]);
          newBoard[from.r][from.c] = "";
          newBoard[to.r][to.c] = piece;
          return newBoard;
        });
      }
    };

    socket.onclose = () => {
      console.log("Disconnected");
      setConnected(false);
    };

    setWs(socket);
    return () => socket.close();
  }, []);

  // V·∫Ω b√†n c·ªù
  useEffect(() => {
    const ctx = canvasRef.current?.getContext("2d");
    if (!ctx) return;

    ctx.clearRect(0, 0, COLS * CELL_SIZE, ROWS * CELL_SIZE);

    for (let r = 0; r < ROWS; r++) {
      for (let c = 0; c < COLS; c++) {
        // V·∫Ω √¥
        ctx.fillStyle = (r + c) % 2 === 0 ? "#E8EBEF" : "#7D8796";
        ctx.fillRect(c * CELL_SIZE, r * CELL_SIZE, CELL_SIZE, CELL_SIZE);

        // Highlight khi ch·ªçn
        if (selected && selected[0] === r && selected[1] === c) {
          ctx.fillStyle = "rgba(255, 255, 0, 0.4)";
          ctx.fillRect(c * CELL_SIZE, r * CELL_SIZE, CELL_SIZE, CELL_SIZE);
        }

        // V·∫Ω qu√¢n c·ªù
        if (board[r][c] !== "") {
          ctx.font = "48px serif";
          ctx.textAlign = "center";
          ctx.textBaseline = "middle";
          ctx.fillStyle = "black";
          ctx.fillText(
            PIECES[board[r][c]] || board[r][c],
            c * CELL_SIZE + CELL_SIZE / 2,
            r * CELL_SIZE + CELL_SIZE / 2
          );
        }
      }
    }
  }, [board, selected]);

  // Click ch·ªçn qu√¢n
  const handleClick = (e: React.MouseEvent) => {
    const rect = (e.target as HTMLCanvasElement).getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const row = Math.floor(y / CELL_SIZE);
    const col = Math.floor(x / CELL_SIZE);

    if (selected) {
      const [r1, c1] = selected;
      const piece = board[r1][c1];
      if (!piece) {
        setSelected(null);
        return;
      }

      // C·∫≠p nh·∫≠t local
      setBoard(prev => {
        const newBoard = prev.map(row => [...row]);
        newBoard[row][col] = piece;
        newBoard[r1][c1] = "";
        return newBoard;
      });

      // G·ª≠i move cho server
      if (ws && ws.readyState === WebSocket.OPEN) {
        ws.send(JSON.stringify({
          type: "move",
          from: { r: r1, c: c1 },
          to: { r: row, c: col },
          piece,
          move: coordToSquare(r1, c1) + coordToSquare(row, col),
        }));
      }

      setSelected(null);
    } else if (board[row][col] !== "") {
      setSelected([row, col]);
    }
  };

  return (
    <div>
      <h2>{connected ? "‚úÖ ƒê√£ k·∫øt n·ªëi" : "üî¥ M·∫•t k·∫øt n·ªëi"}</h2>
      <canvas
        ref={canvasRef}
        width={COLS * CELL_SIZE}
        height={ROWS * CELL_SIZE}
        onClick={handleClick}
      />
    </div>
  );
}
