import { useEffect, useRef, useState } from "react";

export function useChessSocket(roomId: string, onUpdate: (fen: string, turn: "white" | "black", gameOver: null | "white" | "black") => void) {
  const ws = useRef<WebSocket | null>(null);
  const [playerColor, setPlayerColor] = useState<"white" | "black">("white");

  useEffect(() => {
    ws.current = new WebSocket("ws://localhost:8765"); // sửa thành server của bạn

    ws.current.onopen = () => {
      ws.current?.send(JSON.stringify({ type: "join", room: roomId }));
    };

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      switch (data.type) {
        case "assignColor":
          setPlayerColor(data.color); // server gửi trắng hoặc đen
          break;
        case "state":
        case "move":
          // data.fen, data.turn, data.gameOver
          onUpdate(data.fen, data.turn === "w" ? "white" : "black", data.gameOver || null);
          break;
        case "gameOver":
          onUpdate(data.fen, data.turn === "w" ? "white" : "black", data.winner);
          break;
      }
    };

    return () => {
      ws.current?.close();
    };
  }, [roomId, onUpdate]);

  const sendMove = (moveUCI: string) => {
    if (ws.current?.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({ type: "move", move: moveUCI, room: roomId }));
    }
  };

  return { sendMove, playerColor };
}
