import { useState } from "react";
import { useAuth } from "../context/AuthContext";

export default function RegisterPage({ onSwitchToLogin }: { onSwitchToLogin: () => void }) {
  const { register } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleRegister = async () => {
    if (!username || !password) {
      setError("Vui lòng nhập đầy đủ thông tin!");
      return;
    }

    setError("");
    setLoading(true);
    try {
      // Tạo promise timeout 10 giây
      const timeoutPromise = new Promise<boolean>((_, reject) => {
        setTimeout(() => reject(new Error("Timeout")), 10000);
      });

      // Race giữa register và timeout
      const success = await Promise.race([
        register(username, password),
        timeoutPromise
      ]);

      if (success) {
        onSwitchToLogin();
      } else {
        setError("Tên tài khoản đã tồn tại!");
      }
    } catch (err) {
      if (err instanceof Error && err.message === "Timeout") {
        setError("Không thể kết nối tới server. Vui lòng thử lại sau!");
      } else {
        setError("Đã có lỗi xảy ra, vui lòng thử lại!");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-400 to-indigo-600">
      <div className="bg-white rounded-2xl shadow-lg p-8 w-80 text-center">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">Đăng ký tài khoản</h2>
        
        {error && (
          <div className="mb-4 text-sm text-red-600 bg-red-50 p-2 rounded">
            {error}
          </div>
        )}

        <input
          type="text"
          placeholder="Tên đăng nhập"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          disabled={loading}
          className="w-full mb-4 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        <input
          type="password"
          placeholder="Mật khẩu"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          disabled={loading}
          className="w-full mb-6 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        <button
          onClick={handleRegister}
          disabled={loading}
          className={`w-full ${
            loading 
              ? "bg-gray-400 cursor-not-allowed" 
              : "bg-blue-600 hover:bg-blue-700"
          } text-white font-semibold py-2 rounded-lg transition-colors`}
        >
          {loading ? "Đang xử lý..." : "Tạo tài khoản"}
        </button>

        <p className="mt-4 text-sm text-gray-600">
          Đã có tài khoản?{" "}
          <button
            onClick={onSwitchToLogin}
            disabled={loading}
            className="text-blue-600 hover:underline font-medium disabled:opacity-50"
          >
            Đăng nhập
          </button>
        </p>
      </div>
    </div>
  );
}
