// src/utils/soundManager.ts

// Lưu đường dẫn đến các file âm thanh trong thư mục public/sounds
const sounds = {
  check: new Audio("/sounds/check.mp3"),
  checkmate: new Audio("/sounds/checkmate.mp3"),
//   move: new Audio("/sounds/move.mp3"),
//   capture: new Audio("/sounds/capture.mp3"),
};

/**
 * Phát âm thanh tương ứng với sự kiện
 */
export function playSound(event: string) {
  const sound = sounds[event as keyof typeof sounds];
  if (sound) {
    // Đảm bảo âm thanh được phát lại từ đầu mỗi lần
    sound.currentTime = 0;
    sound.play().catch((err) => console.warn("Không thể phát âm thanh:", err));
  }
}
