import { useEffect, useRef, useState } from "react";

export function useChessSocket(
  roomId: string,
  onUpdate: (fen: string, turn: "white" | "black", gameOver: null | "white" | "black", event?: string) => void,
) {
  const ws = useRef<WebSocket | null>(null);
  const [playerColor, setPlayerColor] = useState<"white" | "black" | null>(null);
  const onUpdateRef = useRef(onUpdate);

  useEffect(() => {
    onUpdateRef.current = onUpdate;
  }, [onUpdate]);

  useEffect(() => {
    ws.current = new WebSocket("ws://localhost:8765");

    ws.current.onopen = () => {
      ws.current?.send(JSON.stringify({ type: "join", room: roomId }));
    };

    ws.current.onmessage = (event) => {
      // Tách từng JSON riêng, kể cả khi server gửi dính liền
      const matches = event.data.match(/{[^}]+}/g);
      if (!matches) return;

      for (const jsonStr of matches) {
        try {
          const data = JSON.parse(jsonStr);

          switch (data.type) {
            case "assignColor":
              setPlayerColor(data.color);
              break;
            case "state":
              onUpdateRef.current(
                data.fen,
                data.turn,
                data.gameOver || null,
                data.event || undefined
              );
              break;
            case "error":
              console.warn("Server error:", data.msg);
              break;
            default:
              console.warn("Unknown message type:", data.type);
          }
        } catch (err) {
          console.warn("Invalid JSON:", jsonStr, err);
        }
      }
    };


    ws.current.onclose = () => {
      console.log("Disconnected from server");
    };

    return () => {
      ws.current?.close();
    };
  }, [roomId]);

  const sendMove = (moveUCI: string) => {
    if (!playerColor) return;
    if (ws.current?.readyState === WebSocket.OPEN) {
      const msg = JSON.stringify({ type: "move", move: moveUCI, room: roomId });
      console.log("[Client -> Bridge]", msg);
      ws.current.send(msg);
    }
  };

  return { sendMove, playerColor };
}
