// ChessClient.tsx
import React, { useEffect, useRef, useState } from "react";

const CELL_SIZE = 80;
const ROWS = 8, COLS = 8;
const WS_URL = "ws://192.168.1.9:8765";

export default function ChessClient() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [ws, setWs] = useState<WebSocket | null>(null);
  const [board, setBoard] = useState(
    Array.from({ length: ROWS }, (_, r) =>
      Array.from({ length: COLS }, (_, c) => (r === 1 ? "P" : ""))
    )
  );
  const [selected, setSelected] = useState<[number, number] | null>(null);

  useEffect(() => {
    const socket = new WebSocket(WS_URL);
    socket.onopen = () => {
      console.log("Connected!");
      socket.send(JSON.stringify({ type: "join", room: "room1" }));
    };
    socket.onmessage = (msg) => {
      const data = JSON.parse(msg.data);
      if (data.type === "move") {
        const [r1, c1, r2, c2] = data.move.split(",").map(Number);
        setBoard((prev) => {
          const copy = prev.map((row) => [...row]);
          copy[r2][c2] = copy[r1][c1];
          copy[r1][c1] = "";
          return copy;
        });
      }
    };
    setWs(socket);
    return () => socket.close();
  }, []);

  useEffect(() => {
    const ctx = canvasRef.current?.getContext("2d");
    if (!ctx) return;
    for (let r = 0; r < ROWS; r++) {
      for (let c = 0; c < COLS; c++) {
        ctx.fillStyle = (r + c) % 2 === 0 ? "#E8EBEF" : "#7D8796";
        ctx.fillRect(c * CELL_SIZE, r * CELL_SIZE, CELL_SIZE, CELL_SIZE);
        if (board[r][c] === "P") {
          ctx.beginPath();
          ctx.arc(
            c * CELL_SIZE + CELL_SIZE / 2,
            r * CELL_SIZE + CELL_SIZE / 2,
            CELL_SIZE / 3,
            0,
            2 * Math.PI
          );
          ctx.fillStyle = "black";
          ctx.fill();
        }
      }
    }
  }, [board]);

  const handleClick = (e: React.MouseEvent) => {
    const rect = (e.target as HTMLCanvasElement).getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const row = Math.floor(y / CELL_SIZE);
    const col = Math.floor(x / CELL_SIZE);

    if (selected) {
      const [r1, c1] = selected;
      const move = `${r1},${c1},${row},${col}`;
      ws?.send(JSON.stringify({ type: "move", move }));
      setSelected(null);
    } else if (board[row][col] !== "") {
      setSelected([row, col]);
    }
  };

  return (
    <div>
      <canvas
        ref={canvasRef}
        width={COLS * CELL_SIZE}
        height={ROWS * CELL_SIZE}
        onClick={handleClick}
      />
    </div>
  );
}
