import React, { createContext, useContext, useState } from "react";

type User = { username: string; user_id: string; };

type AuthContextType = {
  user: User | null;
  login: (username: string, password: string) => Promise<boolean>;
  register: (username: string, password: string) => boolean;
  logout: () => void;
};

const AuthContext = createContext<AuthContextType | null>(null);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);

  const login = async (username: string, password: string): Promise<boolean> => {
    return new Promise((resolve) => {
      const proto = window.location.protocol === "https:" ? "wss:" : "ws:";
      const host = process.env.REACT_APP_WS_HOST || (window.location.hostname + ":8765");
      const ws = new WebSocket(`${proto}//${host}`);

      ws.onopen = () => {
        const msg = JSON.stringify({ type: "LOGIN", username, password });
        ws.send(msg);
      };

      ws.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);
          if (data.type === "LOGIN") {
            if (data.success) {
              setUser({
                username,
                user_id: data.user_id
              });
              resolve(true);
            } else {
              resolve(false);
            }
          }
        } catch (e) {
          console.error("Invalid JSON from server:", event.data);
          resolve(false);
        }
        ws.close();
      };

      ws.onerror = () => {
        console.error("WebSocket error");
        resolve(false);
      };
    });
  };

  const register = (username: string, password: string) => {
    // (tuỳ bạn sau này muốn thêm API)
    return true;
  };

  const logout = () => setUser(null);

  return (
    <AuthContext.Provider value={{ user, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext)!;
