import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";

type HomePageProps = {
  onEnterRoom: (roomId: string) => void;
  onLogout: () => void;
};

export default function HomePage({ onEnterRoom, onLogout }: HomePageProps) {
  const { user, logout } = useAuth();
  const [roomId, setRoomId] = useState("");
  const [hoveredBtn, setHoveredBtn] = useState<string | null>(null);
  const [hoveredCard, setHoveredCard] = useState<number | null>(null);

  const styles: Record<string, React.CSSProperties> = {
    page: {
      minHeight: "100vh",
      backgroundColor: "#0d0d0d",
      color: "#f9fafb",
      fontFamily: "'Segoe UI', sans-serif",
      padding: "30px 60px",
      transition: "all 0.3s ease-in-out",
    },
    header: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      borderBottom: "1px solid #1f2937",
      paddingBottom: 10,
      marginBottom: 25,
    },
    logo: {
      fontSize: 22,
      fontWeight: 600,
      color: "#facc15",
      letterSpacing: "0.5px",
      transition: "transform 0.3s ease",
    },
    username: {
      color: "#facc15",
      fontWeight: 600,
    },
    stats: {
      display: "flex",
      gap: 20,
      marginBottom: 30,
    },
    statCard: {
      flex: 1,
      background: "#111827",
      borderRadius: 10,
      textAlign: "center" as const,
      padding: 20,
      boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
      transition: "transform 0.25s ease, box-shadow 0.25s ease",
    },
    statCardHover: {
      transform: "translateY(-4px)",
      boxShadow: "0 6px 16px rgba(250,204,21,0.4)",
    },
    statIcon: {
      fontSize: 24,
      color: "#facc15",
    },
    statValue: {
      fontSize: 22,
      fontWeight: "bold",
      marginTop: 6,
    },
    statLabel: {
      fontSize: 14,
      color: "#9ca3af",
    },
    modes: {
      marginTop: 20,
    },
    modeTitle: {
      marginBottom: 16,
      color: "#d1d5db",
      fontSize: 16,
      letterSpacing: "0.8px",
    },
    modeGrid: {
      display: "flex",
      gap: 20,
      flexWrap: "wrap" as const,
    },
    modeCard: {
      background: "#111827",
      flex: 1,
      minWidth: 280,
      padding: 20,
      borderRadius: 10,
      boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
      transition: "transform 0.25s ease, box-shadow 0.25s ease",
    },
    modeCardHover: {
      transform: "translateY(-6px)",
      boxShadow: "0 8px 18px rgba(250,204,21,0.3)",
    },
    modeIcon: {
      fontSize: 24,
      background: "#27272a",
      width: 40,
      height: 40,
      borderRadius: 8,
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      color: "#facc15",
      marginBottom: 10,
      transition: "transform 0.3s ease",
    },
    modeTitleText: {
      color: "#f9fafb",
      marginBottom: 6,
      fontWeight: 600,
    },
    modeText: {
      color: "#9ca3af",
      fontSize: 14,
      marginBottom: 12,
      lineHeight: 1.4,
    },
    input: {
      width: "100%",
      padding: 8,
      borderRadius: 8,
      border: "1px solid #374151",
      background: "#1f2937",
      color: "white",
      marginBottom: 12,
      outline: "none",
      transition: "border-color 0.2s ease, box-shadow 0.2s ease",
    },
    inputFocus: {
      borderColor: "#facc15",
      boxShadow: "0 0 0 2px rgba(250,204,21,0.2)",
    },
    btn: {
      background: "#f59e0b",
      color: "#fff",
      border: "none",
      borderRadius: 8,
      padding: 10,
      width: "100%",
      cursor: "pointer",
      fontWeight: 600,
      fontSize: 15,
      transition: "all 0.25s ease",
    },
    btnHover: {
      background: "#d97706",
      transform: "scale(1.03)",
    },
    btnGreen: {
      background: "#059669",
    },
    btnGreenHover: {
      background: "#047857",
      transform: "scale(1.03)",
    },
    footer: {
      textAlign: "center" as const,
      marginTop: 30,
      opacity: 0.8,
    },
    logout: {
      color: "#9ca3af",
      background: "none",
      border: "none",
      cursor: "pointer",
      fontSize: 14,
      transition: "color 0.3s ease",
    },
    logoutHover: {
      color: "#facc15",
    },
  };

  return (
    <div style={styles.page}>
      {/* Header */}
      <header style={styles.header}>
        <div
          style={styles.logo}
          onMouseEnter={(e) => ((e.currentTarget.style.transform = "scale(1.05)"))}
          onMouseLeave={(e) => ((e.currentTarget.style.transform = "scale(1)"))}
        >
          ‚ôî Chess Master
        </div>
        <div>
          Ch√†o, <span style={styles.username}>{user?.username || "Ng∆∞·ªùi ch∆°i"}</span>
        </div>
      </header>

      {/* Stats Section */}
      <section style={styles.stats}>
        {[
          { icon: "üèÜ", value: "24", label: "Chi·∫øn Th·∫Øng" },
          { icon: "üî•", value: "67%", label: "T·ª∑ L·ªá Th·∫Øng" },
          { icon: "üìà", value: "1650", label: "X·∫øp H·∫°ng" },
        ].map((stat, i) => (
          <div
            key={i}
            style={{
              ...styles.statCard,
              ...(hoveredCard === i ? styles.statCardHover : {}),
            }}
            onMouseEnter={() => setHoveredCard(i)}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.statIcon}>{stat.icon}</div>
            <div style={styles.statValue}>{stat.value}</div>
            <div style={styles.statLabel}>{stat.label}</div>
          </div>
        ))}
      </section>

      {/* Modes Section */}
      <section style={styles.modes}>
        <h3 style={styles.modeTitle}>CH·∫æ ƒê·ªò CH∆†I</h3>
        <div style={styles.modeGrid}>
          {/* Mode 1 */}
          <div
            style={{
              ...styles.modeCard,
              ...(hoveredCard === 3 ? styles.modeCardHover : {}),
            }}
            onMouseEnter={() => setHoveredCard(3)}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.modeIcon}>üë•</div>
            <h4 style={styles.modeTitleText}>Ch∆°i C√πng B·∫°n</h4>
            <p style={styles.modeText}>
              Gh√©p tr·∫≠n v·ªõi b·∫°n b√®. Nh·∫≠p m√£ ph√≤ng ƒë·ªÉ ch∆°i ho·∫∑c t·∫°o ph√≤ng m·ªõi cho b·∫°n b√® tham gia.
            </p>
            <input
              type="text"
              placeholder="Nh·∫≠p m√£ ph√≤ng"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
              style={styles.input}
            />
            <button
              style={{
                ...styles.btn,
                ...(hoveredBtn === "room" ? styles.btnHover : {}),
              }}
              onMouseEnter={() => setHoveredBtn("room")}
              onMouseLeave={() => setHoveredBtn(null)}
              onClick={() => onEnterRoom(roomId)}
            >
              Nh·∫≠p M√£ Ph√≤ng
            </button>
          </div>

          {/* Mode 2 */}
          <div
            style={{
              ...styles.modeCard,
              ...(hoveredCard === 4 ? styles.modeCardHover : {}),
            }}
            onMouseEnter={() => setHoveredCard(4)}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.modeIcon}>‚ö°</div>
            <h4 style={styles.modeTitleText}>Ch∆°i Li√™n Minh</h4>
            <p style={styles.modeText}>T√¨m ƒë·ªëi th·ªß ng·∫´u nhi√™n c√≥ c√πng tr√¨nh ƒë·ªô ƒë·ªÉ thi ƒë·∫•u.</p>
            <button
              style={{
                ...styles.btn,
