import { useEffect, useRef, useState } from "react";

export function useChessSocket(
  roomId: string,
  onUpdate: (fen: string, turn: "white" | "black", gameOver: null | "white" | "black") => void
) {
  const ws = useRef<WebSocket | null>(null);
  const [playerColor, setPlayerColor] = useState<"white" | "black" | null>(null);

    useEffect(() => {
    ws.current = new WebSocket("ws://localhost:8765");

    ws.current.onopen = () => {
        ws.current?.send(JSON.stringify({ type: "join", room: roomId }));
    };

    ws.current.onmessage = (event) => {
        const data = JSON.parse(event.data);

        switch (data.type) {
        case "assignColor":
            setPlayerColor(data.color);
            break;

        case "state":
            onUpdate(data.fen, data.turn, data.gameOver || null);
            break;

        case "error":
            alert(data.msg);
            break;

        default:
            console.warn("Unknown message type:", data.type);
        }
    };

    ws.current.onclose = () => {
        console.log("Disconnected from server");
    };

    return () => {
        ws.current?.close();
    };
    }, [roomId, onUpdate]); // ❌ bỏ playerColor ra khỏi đây


  const sendMove = (moveUCI: string) => {
    if (ws.current?.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({ type: "move", move: moveUCI, room: roomId }));
    }
  };

  return { sendMove, playerColor };
}
