import React from "react";

interface InfoPanelProps {
  roomId: string;
  turn: "white" | "black";
  playerColor: "white" | "black" | null;
  gameOver: "white" | "black" | null;
}

export const InfoPanel: React.FC<InfoPanelProps> = ({ roomId, turn, playerColor, gameOver }) => {
  // Màu thể hiện lượt hiện tại
  const turnColor = turn === "white" ? "#f5f5f5" : "#333";
  const turnBg = turn === "white" ? "#b0d8ff" : "#ffd8b0";

  return (
    <div
      style={{
        width: "300px",
        padding: "20px",
        border: "2px solid #333",
        borderRadius: "10px",
        backgroundColor: "#fafafa",
        boxShadow: "3px 3px 8px rgba(0,0,0,0.2)",
        fontFamily: "sans-serif",
      }}
    >
      <h3 style={{ textAlign: "center", marginBottom: "15px" }}>Thông tin phòng</h3>

      <p>
        <strong>Mã phòng:</strong> <span>{roomId}</span>
      </p>

      {/* Hiển thị lượt hiện tại với màu */}
      <p
        style={{
          fontWeight: "bold",
          backgroundColor: turnBg,
          color: turnColor,
          padding: "6px 10px",
          borderRadius: "6px",
          textAlign: "center",
          marginTop: "10px",
        }}
      >
        Lượt hiện tại: {turn === "white" ? "Trắng ♔" : "Đen ♚"}
      </p>

      {/* Người chơi */}
      <div style={{ marginTop: "20px" }}>
        <h4 style={{ borderBottom: "1px solid #ccc", paddingBottom: "4px" }}>Người chơi</h4>
        <p>♔ <strong>Trắng:</strong> {playerColor === "white" ? "Bạn" : "Đối thủ"}</p>
        <p>♚ <strong>Đen:</strong> {playerColor === "black" ? "Bạn" : "Đối thủ"}</p>
      </div>

      {/* Trạng thái kết thúc */}
      {gameOver && (
        <div
          style={{
            marginTop: "30px",
            color: gameOver === "white" ? "#2b6cb0" : gameOver === "black" ? "#b83232" : "#333",
            textAlign: "center",
            fontWeight: "bold",
            fontSize: "18px",
          }}
        >
          {gameOver === "white"
            ? "Trắng thắng! ♔"
            : gameOver === "black"
            ? "Đen thắng! ♚"
            : "Hòa!"}
        </div>
      )}
    </div>
  );
};
