type SquareProps = {
  value: string;
  isSelected: boolean;
  onClick: () => void;
  color: string;
};

export default function Square({ value, isSelected, onClick, color }: SquareProps) {
  return (
    <div
      onClick={onClick}
      style={{
        width: "60px",
        height: "60px",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: isSelected ? "yellow" : color,
        cursor: "pointer",
      }}
    >
      {value && <img src={value} alt="" style={{ width: "50px", height: "50px" }} />}
    </div>
  );
}
