import { useState } from "react";
import Square from "./Square";

const pieceImages: { [key: string]: string } = {
  P: "/pieces/wP.png",
  p: "/pieces/bP.png",
  R: "/pieces/wR.png",
  r: "/pieces/bR.png",
  N: "/pieces/wN.png",
  n: "/pieces/bN.png",
  B: "/pieces/wB.png",
  b: "/pieces/bB.png",
  Q: "/pieces/wQ.png",
  q: "/pieces/bQ.png",
  K: "/pieces/wK.png",
  k: "/pieces/bK.png",
};

const initialBoard = [
  "rnbqkbnr",
  "pppppppp",
  "........",
  "........",
  "........",
  "........",
  "PPPPPPPP",
  "RNBQKBNR",
];

const Board = () => {
  const [board, setBoard] = useState(initialBoard.map(row => row.split("")));
  const [selected, setSelected] = useState<[number, number] | null>(null);

  const handleClick = (r: number, c: number) => {
    if (selected) {
      const [r1, c1] = selected;
      const piece = board[r1][c1];
      if (!piece || piece === ".") return setSelected(null);

      const newBoard = board.map(row => [...row]);
      newBoard[r1][c1] = ".";
      newBoard[r][c] = piece;
      setBoard(newBoard);
      setSelected(null);
    } else {
      if (board[r][c] !== ".") setSelected([r, c]);
    }
  };

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(8, 60px)",
        gridTemplateRows: "repeat(8, 60px)",
        border: "2px solid black",
      }}
    >
      {board.map((row, i) =>
        row.map((piece, j) => {
          const color = (i + j) % 2 === 0 ? "#f0d9b5" : "#b58863";
          const piecePath = pieceImages[piece] || "";
          const isSelected = selected?.[0] === i && selected?.[1] === j;
          return (
            <Square
              key={`${i}-${j}`}
              value={piecePath}
              isSelected={isSelected}
              onClick={() => handleClick(i, j)}
              color={color}
            />
          );
        })
      )}
    </div>
  );
};

export default Board;
