import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import "./RegisterPage.css"; // üëà import file CSS m·ªõi

export default function RegisterPage({ onSwitchToLogin }: { onSwitchToLogin: () => void }) {
  const { register } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [shake, setShake] = useState(false);

  const handleRegister = async () => {
    if (!username || !password) {
      setError("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin!");
      triggerShake();
      return;
    }

    setError("");
    setLoading(true);
    try {
      const timeoutPromise = new Promise<boolean>((_, reject) => {
        setTimeout(() => reject(new Error("Timeout")), 10000);
      });

      const success = await Promise.race([
        register(username, password),
        timeoutPromise
      ]);

      if (success) {
        onSwitchToLogin();
      } else {
        setError("T√™n t√†i kho·∫£n ƒë√£ t·ªìn t·∫°i!");
        triggerShake();
      }
    } catch (err) {
      if (err instanceof Error && err.message === "Timeout") {
        setError("Kh√¥ng th·ªÉ k·∫øt n·ªëi t·ªõi server. Vui l√≤ng th·ª≠ l·∫°i sau!");
      } else {
        setError("ƒê√£ c√≥ l·ªói x·∫£y ra, vui l√≤ng th·ª≠ l·∫°i!");
      }
      triggerShake();
    } finally {
      setLoading(false);
    }
  };

  const triggerShake = () => {
    setShake(true);
    setTimeout(() => setShake(false), 500);
  };

  return (
    <div className="register-page">
      <div className={`register-card ${shake ? "shake" : ""}`}>
        <h2 className="title">ƒêƒÉng k√Ω t√†i kho·∫£n</h2>

        {error && <div className="error-message">{error}</div>}

        <input
          type="text"
          placeholder="T√™n ƒëƒÉng nh·∫≠p"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          disabled={loading}
          className="input-field"
        />

        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          disabled={loading}
          className="input-field"
        />

        <button
          onClick={handleRegister}
          disabled={loading}
          className={`submit-btn ${loading ? "disabled" : ""}`}
        >
          {loading ? "ƒêang x·ª≠ l√Ω..." : "T·∫°o t√†i kho·∫£n"}
        </button>

        <p className="login-text">
          ƒê√£ c√≥ t√†i kho·∫£n?{" "}
          <button
            onClick={onSwitchToLogin}
            disabled={loading}
            className="login-link"
          >
            ƒêƒÉng nh·∫≠p
          </button>
        </p>
      </div>
    </div>
  );
}
