import { useState } from "react";
import { useAuth } from "../context/AuthContext";

export default function RegisterPage({ onSwitchToLogin }: { onSwitchToLogin: () => void }) {
  const { register } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = () => {
    if (register(username, password)) {
      alert("Đăng ký thành công!");
      onSwitchToLogin();
    }
  };

  return (
    <div>
      <h2>Đăng ký</h2>
      <input placeholder="Tên đăng nhập" value={username} onChange={e => setUsername(e.target.value)} />
      <input placeholder="Mật khẩu" type="password" value={password} onChange={e => setPassword(e.target.value)} />
      <button onClick={handleRegister}>Tạo tài khoản</button>
      <button onClick={onSwitchToLogin}>Đã có tài khoản? Đăng nhập</button>
    </div>
  );
}
