import React, { useEffect, useRef, useState } from "react";

const CELL_SIZE = 80;
const ROWS = 8, COLS = 8;
const WS_URL = "ws://192.168.1.9:8765";

// Map k√Ω hi·ªáu qu√¢n c·ªù sang ƒë∆∞·ªùng d·∫´n ·∫£nh
const PIECE_IMAGES: Record<string, string> = {
  P: "/pieces/wP.png",
  R: "/pieces/wR.png",
  N: "/pieces/wN.png",
  B: "/pieces/wB.png",
  Q: "/pieces/wQ.png",
  K: "/pieces/wK.png",
  p: "/pieces/bP.png",
  r: "/pieces/bR.png",
  n: "/pieces/bN.png",
  b: "/pieces/bB.png",
  q: "/pieces/bQ.png",
  k: "/pieces/bK.png",
};

// Chuy·ªÉn (row, col) ‚Üí √¥ c·ªù d·∫°ng "a1", "b3", ...
function coordToSquare(row: number, col: number) {
  const file = String.fromCharCode("a".charCodeAt(0) + col);
  const rank = 8 - row;
  return `${file}${rank}`;
}

export default function ChessClient() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [ws, setWs] = useState<WebSocket | null>(null);
  const [connected, setConnected] = useState(false);

  // B√†n c·ªù 8x8
  const [board, setBoard] = useState(
    Array.from({ length: ROWS }, () =>
      Array.from({ length: COLS }, () => "")
    )
  );

  const [selected, setSelected] = useState<[number, number] | null>(null);
  const pieceImagesRef = useRef<Record<string, HTMLImageElement>>({});

  // üîπ Load ·∫£nh qu√¢n c·ªù ch·ªâ 1 l·∫ßn
  useEffect(() => {
    const images: Record<string, HTMLImageElement> = {};
    for (const [key, src] of Object.entries(PIECE_IMAGES)) {
      const img = new Image();
      img.src = src;
      images[key] = img;
    }
    pieceImagesRef.current = images;
  }, []);

  // üîπ K·∫øt n·ªëi WebSocket
  useEffect(() => {
    const socket = new WebSocket(WS_URL);

    socket.onopen = () => {
      console.log("Connected to server");
      setConnected(true);
      socket.send(JSON.stringify({ type: "join", room: "room1" }));
    };

    socket.onmessage = (msg) => {
      const data = JSON.parse(msg.data);
      console.log("Received:", data);

      if (data.type === "board") setBoard(data.board);
      if (data.type === "move") {
        const { from, to, piece } = data;
        setBoard(prev => {
          const newBoard = prev.map(row => [...row]);
          newBoard[from.r][from.c] = "";
          newBoard[to.r][to.c] = piece;
          return newBoard;
        });
      }
    };

    socket.onclose = () => setConnected(false);
    setWs(socket);
    return () => socket.close();
  }, []);

  // üîπ V·∫Ω b√†n c·ªù v√† qu√¢n c·ªù
  useEffect(() => {
    const ctx = canvasRef.current?.getContext("2d");
    if (!ctx) return;

    ctx.clearRect(0, 0, COLS * CELL_SIZE, ROWS * CELL_SIZE);

    for (let r = 0; r < ROWS; r++) {
      for (let c = 0; c < COLS; c++) {
        // √î n·ªÅn
        ctx.fillStyle = (r + c) % 2 === 0 ? "#E8EBEF" : "#7D8796";
        ctx.fillRect(c * CELL_SIZE, r * CELL_SIZE, CELL_SIZE, CELL_SIZE);

        // Highlight √¥ ƒë∆∞·ª£c ch·ªçn
        if (selected && selected[0] === r && selected[1] === c) {
          ctx.fillStyle = "rgba(255, 255, 0, 0.4)";
          ctx.fillRect(c * CELL_SIZE, r * CELL_SIZE, CELL_SIZE, CELL_SIZE);
        }

        // Qu√¢n c·ªù
        const piece = board[r][c];
        if (piece && pieceImagesRef.current[piece]) {
          const img = pieceImagesRef.current[piece];
          ctx.drawImage(
            img,
            c * CELL_SIZE + 10,
            r * CELL_SIZE + 10,
            CELL_SIZE - 20,
            CELL_SIZE - 20
          );
        }
      }
    }
  }, [board, selected]);

  // üîπ X·ª≠ l√Ω click chu·ªôt
  const handleClick = (e: React.MouseEvent) => {
    const rect = (e.target as HTMLCanvasElement).getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const row = Math.floor(y / CELL_SIZE);
    const col = Math.floor(x / CELL_SIZE);

    if (selected) {
      const [r1, c1] = selected;
      const piece = board[r1][c1];
      if (!piece) {
        setSelected(null);
        return;
      }

      // C·∫≠p nh·∫≠t local
      setBoard(prev => {
        const newBoard = prev.map(row => [...row]);
        newBoard[row][col] = piece;
        newBoard[r1][c1] = "";
        return newBoard;
      });

      // G·ª≠i move cho server
      if (ws && ws.readyState === WebSocket.OPEN) {
        ws.send(JSON.stringify({
          type: "move",
          from: { r: r1, c: c1 },
          to: { r: row, c: col },
          piece,
          move: coordToSquare(r1, c1) + coordToSquare(row, col),
        }));
      }

      setSelected(null);
    } else if (board[row][col] !== "") {
      setSelected([row, col]);
    }
  };

  return (
    <div className="flex flex-col items-center mt-4">
      <h2 className="text-xl mb-2">
        {connected ? "‚úÖ ƒê√£ k·∫øt n·ªëi" : "üî¥ M·∫•t k·∫øt n·ªëi"}
      </h2>
      <canvas
        ref={canvasRef}
        width={COLS * CELL_SIZE}
        height={ROWS * CELL_SIZE}
        onClick={handleClick}
        className="shadow-lg rounded-lg"
      />
    </div>
  );
}
