import { useState } from "react";
import Square from "./Square";

const pieceImages: { [key: string]: string } = {
  P: "/pieces/wP.png",
  p: "/pieces/bP.png",
  R: "/pieces/wR.png",
  r: "/pieces/bR.png",
  N: "/pieces/wN.png",
  n: "/pieces/bN.png",
  B: "/pieces/wB.png",
  b: "/pieces/bB.png",
  Q: "/pieces/wQ.png",
  q: "/pieces/bQ.png",
  K: "/pieces/wK.png",
  k: "/pieces/bK.png",
};

const initialBoard: string[] = [
  "rnbqkbnr",
  "pppppppp",
  "........",
  "........",
  "........",
  "........",
  "PPPPPPPP",
  "RNBQKBNR",
];

// Hàm kiểm tra di chuyển hợp lệ
function isValidMove(
  piece: string,
  r1: number,
  c1: number,
  r2: number,
  c2: number,
  board: string[][]
): boolean {
  const target = board[r2][c2];

  // Không ăn quân cùng màu
  if (piece === piece.toUpperCase() && target !== "." && target === target.toUpperCase()) return false;
  if (piece === piece.toLowerCase() && target !== "." && target === target.toLowerCase()) return false;

  switch (piece.toLowerCase()) {
    case "p": { // Tốt
      const direction = piece === "P" ? -1 : 1; // Trắng đi lên, đen đi xuống
      const startRow = piece === "P" ? 6 : 1;

      // Đi thẳng 1 ô
      if (c1 === c2 && r2 === r1 + direction && board[r2][c2] === ".") return true;

      // Đi thẳng 2 ô lần đầu
      if (c1 === c2 && r1 === startRow && r2 === r1 + 2 * direction && board[r1 + direction][c1] === "." && board[r2][c2] === ".") return true;

      // Ăn chéo
      if (Math.abs(c2 - c1) === 1 && r2 === r1 + direction && target !== ".") return true;

      return false;
    }
    case "r": // Xe
      if (r1 === r2) {
        const min = Math.min(c1, c2) + 1;
        const max = Math.max(c1, c2);
        for (let c = min; c < max; c++) if (board[r1][c] !== ".") return false;
        return true;
      } else if (c1 === c2) {
        const min = Math.min(r1, r2) + 1;
        const max = Math.max(r1, r2);
        for (let r = min; r < max; r++) if (board[r][c1] !== ".") return false;
        return true;
      }
      return false;
    case "n": // Mã
      return (Math.abs(r2 - r1) === 2 && Math.abs(c2 - c1) === 1) ||
             (Math.abs(r2 - r1) === 1 && Math.abs(c2 - c1) === 2);
    case "b": // Tượng
      if (Math.abs(r2 - r1) !== Math.abs(c2 - c1)) return false;
      const dr = r2 > r1 ? 1 : -1;
      const dc = c2 > c1 ? 1 : -1;
      let r = r1 + dr, c = c1 + dc;
      while (r !== r2 && c !== c2) {
        if (board[r][c] !== ".") return false;
        r += dr;
        c += dc;
      }
      return true;
    case "q": // Hậu
      return isValidMove(piece === "Q" ? "R" : "r", r1, c1, r2, c2, board) ||
             isValidMove(piece === "Q" ? "B" : "b", r1, c1, r2, c2, board);
    case "k": // Vua
      return Math.abs(r2 - r1) <= 1 && Math.abs(c2 - c1) <= 1;
    default:
      return false;
  }
}

const Board: React.FC = () => {
  const [board, setBoard] = useState<string[][]>(initialBoard.map(row => row.split("")));
  const [selected, setSelected] = useState<[number, number] | null>(null);

  const handleClick = (r: number, c: number) => {
    if (selected) {
      const [r1, c1] = selected;
      const piece = board[r1][c1];
      if (!piece || piece === ".") return setSelected(null);

      if (isValidMove(piece, r1, c1, r, c, board)) {
        const newBoard = board.map(row => [...row]);
        newBoard[r1][c1] = ".";
        newBoard[r][c] = piece;
        setBoard(newBoard);
      }
      setSelected(null); // bỏ chọn dù di chuyển thành công hay không
    } else {
      if (board[r][c] !== ".") setSelected([r, c]);
    }
  };

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(8, 60px)",
        gridTemplateRows: "repeat(8, 60px)",
        border: "2px solid black",
      }}
    >
      {board.map((row, i) =>
        row.map((piece, j) => {
          const color = (i + j) % 2 === 0 ? "#f0d9b5" : "#b58863";
          const piecePath = pieceImages[piece] || "";
          const isSelected = selected?.[0] === i && selected?.[1] === j;
          return (
            <Square
              key={`${i}-${j}`}
              value={piecePath}
              isSelected={isSelected}
              onClick={() => handleClick(i, j)}
              color={color}
            />
          );
        })
      )}
    </div>
  );
};

export default Board;
