export const InfoPanel = ({ roomId, turn, playerColor, gameOver }: any) => (
  <div
    style={{
      width: "300px",
      padding: "20px",
      border: "2px solid #333",
      borderRadius: "10px",
      backgroundColor: "#fafafa",
    }}
  >
    <h3 style={{ textAlign: "center" }}>Thông tin phòng</h3>
    <p><strong>Mã phòng:</strong> {roomId}</p>
    <p><strong>Lượt hiện tại:</strong> {turn === "white" ? "Trắng" : "Đen"}</p>
    <div style={{ marginTop: "20px" }}>
      <h4>Người chơi</h4>
      <p>♔ <strong>Trắng:</strong> {playerColor === "white" ? "Bạn" : "Đối thủ"}</p>
      <p>♚ <strong>Đen:</strong> {playerColor === "black" ? "Bạn" : "Đối thủ"}</p>
    </div>
    {gameOver && (
      <div style={{ marginTop: "30px", color: "red", textAlign: "center", fontWeight: "bold" }}>
        {gameOver === "white" ? "Trắng thắng!" : gameOver === "black" ? "Đen thắng!" : "Hòa!"}
      </div>
    )}
  </div>
);
