import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";

interface HomePageProps {
  onEnterRoom: (roomId: string) => void;
}

const HomePage: React.FC<HomePageProps> = ({ onEnterRoom }) => {
  const { user, logout } = useAuth();
  const [roomId, setRoomId] = useState("");

  const styles: { [k: string]: React.CSSProperties } = {
    page: {
      minHeight: "100vh",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      background: "linear-gradient(135deg, #c7d2fe 0%, #e0e7ff 100%)",
      padding: 20,
      fontFamily: "'Segoe UI', sans-serif",
    },
    card: {
      width: 420,
      background: "#ffffff",
      padding: 28,
      borderRadius: 16,
      boxShadow: "0 6px 20px rgba(0,0,0,0.1)",
      textAlign: "center",
      transition: "transform 0.2s ease",
    },
    avatar: {
      width: 70,
      height: 70,
      borderRadius: "50%",
      background: "#4f46e5",
      color: "#fff",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: 26,
      fontWeight: 700,
      margin: "0 auto 14px",
    },
    title: {
      margin: "6px 0 20px",
      color: "#1e293b",
      fontSize: 22,
      fontWeight: 600,
    },
    input: {
      width: "100%",
      padding: "10px 14px",
      borderRadius: 8,
      border: "1px solid #d1d5db",
      marginBottom: 14,
      fontSize: 15,
      outline: "none",
      transition: "border-color 0.2s ease",
    },
    actions: {
      display: "flex",
      gap: 10,
      marginBottom: 16,
    },
    btn: {
      flex: 1,
      padding: "10px 12px",
      borderRadius: 8,
      border: "none",
      cursor: "pointer",
      fontWeight: 600,
      fontSize: 15,
      transition: "background 0.2s ease, transform 0.2s ease",
    },
    primary: {
      background: "#4f46e5",
      color: "#fff",
    },
    secondary: {
      background: "#fff",
      color: "#1e293b",
      border: "1px solid #d1d5db",
    },
    logout: {
      marginTop: 10,
      fontSize: 14,
      color: "#6b7280",
      background: "transparent",
      border: "none",
      cursor: "pointer",
    },
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <div style={styles.avatar}>
          {user?.username?.charAt(0)?.toUpperCase() || "U"}
        </div>
        <h2 style={styles.title}>Xin chào {user?.username}!</h2>

        <input
          style={styles.input}
          placeholder="Nhập mã phòng"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
          aria-label="Mã phòng"
        />

        <div style={styles.actions}>
          <button
            style={{ ...styles.btn, ...styles.primary }}
            onClick={() => onEnterRoom(roomId)}
          >
            Vào phòng
          </button>
          <button
            style={{ ...styles.btn, ...styles.secondary }}
            onClick={() => onEnterRoom("auto")}
          >
            Ghép ngẫu nhiên
          </button>
        </div>

        <button style={styles.logout} onClick={logout}>
          Đăng xuất
        </button>
      </div>
    </div>
  );
};

export default HomePage;
