import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import "./LoginPage.css";

export default function LoginPage({ onSwitchToRegister }: { onSwitchToRegister: () => void }) {
  const { login } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async () => {
    const success = await login(username, password);
    if (!success) {
      setError("Đăng nhập thất bại! Vui lòng kiểm tra lại.");
    } else {
      setError("");
    }
  };

  return (
    <div className="login-page">
      <div className="login-left">
        <h1 className="login-heading">Chơi Cờ Vua<br />Trực Tuyến</h1>
        <p className="login-subtitle">
          Thách đấu với các tay chơi trên toàn thế giới, cải thiện kỹ năng của bạn
          và trở thành một bậc thầy cờ vua.
        </p>

        <div className="login-box">
          <label className="login-label">Tên đăng nhập</label>
          <input
            className="login-input"
            placeholder="Nhập tên đăng nhập"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />

          <label className="login-label">Mật khẩu</label>
          <input
            className="login-input"
            placeholder="Nhập mật khẩu"
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />

          {error && <p className="login-error">{error}</p>}

          <button className="login-button" onClick={handleLogin}>
            Đăng Nhập
          </button>
        </div>

        <div className="register-section">
          <p>Chưa có tài khoản?</p>
          <button className="register-button" onClick={onSwitchToRegister}>
            Đăng Kí
          </button>
        </div>
      </div>

      <div className="login-right">
        <img
          src="https://kurtmeyer.com/wp-content/uploads/2020/11/Board-with-pieces-03-1.jpg"
          alt="Chess board"
          className="chess-image"
        />
      </div>
    </div>
  );
}
