import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import "./LoginPage.css";

export default function LoginPage({ onSwitchToRegister }: { onSwitchToRegister: () => void }) {
  const { login } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async () => {
    const success = await login(username, password);
    if (!success) {
      setError("Đăng nhập thất bại! Vui lòng kiểm tra lại.");
    } else {
      setError("");
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-left">
          <h1 className="login-title">♟️ Chess Arena</h1>
          <p className="login-description">
            Thách đấu cùng các cao thủ trên toàn thế giới và trở thành bậc thầy cờ vua.
          </p>

          <div className="form-group">
            <label>Tên đăng nhập</label>
            <input
              type="text"
              placeholder="Nhập tên đăng nhập"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />

            <label>Mật khẩu</label>
            <input
              type="password"
              placeholder="Nhập mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />

            {error && <p className="error-text">{error}</p>}

            <button className="btn-primary" onClick={handleLogin}>
              Đăng Nhập
            </button>
          </div>

          <div className="register-section">
            <p>Chưa có tài khoản?</p>
            <button className="btn-secondary" onClick={onSwitchToRegister}>
              Đăng Kí
            </button>
          </div>
        </div>

        <div className="login-right">
          <img
            src="https://kurtmeyer.com/wp-content/uploads/2020/11/Board-with-pieces-03-1.jpg"
            alt="Chess board"
            className="login-image"
          />
        </div>
      </div>
    </div>
  );
}
