import { useState } from "react";
import Square from "./Square";
import { isValidMove, KingMoved, RookMoved } from "./chessRules";

const pieceImages: { [key: string]: string } = {
  P: "/pieces/wP.png",
  p: "/pieces/bP.png",
  R: "/pieces/wR.png",
  r: "/pieces/bR.png",
  N: "/pieces/wN.png",
  n: "/pieces/bN.png",
  B: "/pieces/wB.png",
  b: "/pieces/bB.png",
  Q: "/pieces/wQ.png",
  q: "/pieces/bQ.png",
  K: "/pieces/wK.png",
  k: "/pieces/bK.png",
};

const initialBoard: string[] = [
  "rnbqkbnr",
  "pppppppp",
  "........",
  "........",
  "........",
  "........",
  "PPPPPPPP",
  "RNBQKBNR",
];

const Board: React.FC = () => {
  const [board, setBoard] = useState<string[][]>(initialBoard.map(row => row.split("")));
  const [selected, setSelected] = useState<[number, number] | null>(null);
  const [validMoves, setValidMoves] = useState<[number, number][]>([]);
  const [turn, setTurn] = useState<"white" | "black">("white");
  const [kingMoved, setKingMoved] = useState<KingMoved>({ white: false, black: false });
  const [rookMoved, setRookMoved] = useState<RookMoved>({ wA: false, wH: false, bA: false, bH: false });
  const [gameOver, setGameOver] = useState<null | "white" | "black">(null);


  const handleClick = (r: number, c: number) => {
    if (gameOver) return; // game đã kết thúc, không di chuyển nữa

    const piece = board[r][c];
    const pieceColor = piece.toUpperCase() === piece ? "white" : "black";

    // Nếu chưa chọn quân
    if (!selected) {
      if (piece !== "." && pieceColor === turn) {
        setSelected([r, c]);
        // Tính tất cả ô hợp lệ
        const moves: [number, number][] = [];
        for (let i = 0; i < 8; i++) {
          for (let j = 0; j < 8; j++) {
            if (isValidMove(piece, r, c, i, j, board, kingMoved, rookMoved)) {
              moves.push([i, j]);
            }
          }
        }
        setValidMoves(moves);
      }
      return;
    }

    const [r1, c1] = selected;
    const selectedPiece = board[r1][c1];
    if (!selectedPiece || selectedPiece === ".") {
      setSelected(null);
      setValidMoves([]);
      return;
    }

    // Nếu click vào ô hợp lệ
    if (validMoves.some(([x, y]) => x === r && y === c)) {
      const newBoard = board.map(row => [...row]);
      newBoard[r1][c1] = ".";

      // Castling
      if ((selectedPiece === "K" || selectedPiece === "k") && Math.abs(c - c1) === 2) {
        if (selectedPiece === "K") {
          if (c === 6) { newBoard[7][5] = "R"; newBoard[7][7] = "."; setKingMoved({ ...kingMoved, white: true }); setRookMoved({ ...rookMoved, wH: true }); }
          if (c === 2) { newBoard[7][3] = "R"; newBoard[7][0] = "."; setKingMoved({ ...kingMoved, white: true }); setRookMoved({ ...rookMoved, wA: true }); }
        } else {
          if (c === 6) { newBoard[0][5] = "r"; newBoard[0][7] = "."; setKingMoved({ ...kingMoved, black: true }); setRookMoved({ ...rookMoved, bH: true }); }
          if (c === 2) { newBoard[0][3] = "r"; newBoard[0][0] = "."; setKingMoved({ ...kingMoved, black: true }); setRookMoved({ ...rookMoved, bA: true }); }
        }
      }

      // Promotion tự động
      if (selectedPiece.toLowerCase() === "p" && (r === 0 || r === 7)) {
        newBoard[r][c] = selectedPiece === "P" ? "Q" : "q";
      } else {
        newBoard[r][c] = selectedPiece;
      }

      // Cập nhật trạng thái vua/xe nếu di chuyển
      if (selectedPiece === "K") setKingMoved({ ...kingMoved, white: true });
      if (selectedPiece === "k") setKingMoved({ ...kingMoved, black: true });
      if (selectedPiece === "R" && r1 === 7 && c1 === 0) setRookMoved({ ...rookMoved, wA: true });
      if (selectedPiece === "R" && r1 === 7 && c1 === 7) setRookMoved({ ...rookMoved, wH: true });
      if (selectedPiece === "r" && r1 === 0 && c1 === 0) setRookMoved({ ...rookMoved, bA: true });
      if (selectedPiece === "r" && r1 === 0 && c1 === 7) setRookMoved({ ...rookMoved, bH: true });

      newBoard[r][c] = selectedPiece;

      // Kiểm tra xem còn vua nào không
      const flatBoard = newBoard.flat();
      if (!flatBoard.includes("K")) {
        setGameOver("black"); // đen thắng
      } else if (!flatBoard.includes("k")) {
        setGameOver("white"); // trắng thắng
      }


      setBoard(newBoard);
      setTurn(turn === "white" ? "black" : "white");
    }

    // Reset selection & highlight
    setSelected(null);
    setValidMoves([]);
  };

  return (

    <div style={{ display: "grid", gridTemplateColumns: "repeat(8, 60px)", gridTemplateRows: "repeat(8, 60px)", border: "2px solid black" }}>
      {board.map((row, i) =>
        row.map((piece, j) => {
          const color = (i + j) % 2 === 0 ? "#f0d9b5" : "#b58863";
          const piecePath = pieceImages[piece] || "";
          const isSelected = selected?.[0] === i && selected?.[1] === j;
          const isValid = validMoves.some(([x, y]) => x === i && y === j);
          const bgColor = isSelected ? "yellow" : isValid ? "#a0d8ff" : color;
          return <Square key={`${i}-${j}`} value={piecePath} isSelected={isSelected} onClick={() => handleClick(i, j)} color={bgColor} />;
        })
      )}
    </div>
  );
};

export default Board;
