import { useState } from "react";
import { useAuth } from "../context/AuthContext";

export default function HomePage({ onEnterRoom }: { onEnterRoom: (roomId: string) => void }) {
  const { user, logout } = useAuth();
  const [roomId, setRoomId] = useState("");

  return (
    <div>
      <h2>Xin chào {user?.username}!</h2>
      <p>Điểm ELO: {user?.elo}</p>

      <input placeholder="Nhập mã phòng" value={roomId} onChange={e => setRoomId(e.target.value)} />
      <button onClick={() => onEnterRoom(roomId)}>Vào phòng</button>
      <button onClick={() => onEnterRoom("auto")}>Ghép ngẫu nhiên</button>

      <button onClick={logout}>Đăng xuất</button>
    </div>
  );
}
